            'position':'fixed',
            'top': '-' + elemSctop + 'px',
        });
    }
}

function enableBodyScroll(){
    if(!scrollLocation){
        var bodyOffsetTop = $('body').offset().top;
        $('body').css({
            'position':'relative',
            'top': '0px',
        });
        $('body, html').scrollTop(-bodyOffsetTop);
    }
}
```

