query TagsListQuery {
  site {
    siteMetadata {
      title
      subtitle
    }
  }
  allMarkdownRemark(filter: {frontmatter: {template: {eq: "post"}, draft: {ne: true}}}) {
    group(field: frontmatter___tags) {
      fieldValue
      totalCount
    }
  }
}

query CategoriesListQuery {
  site {
    siteMetadata {
      title
      subtitle
    }
  }
  allMarkdownRemark(filter: {frontmatter: {template: {eq: "post"}, draft: {ne: true}}}) {
    group(field: frontmatter___category) {
      fieldValue
      totalCount
    }
  }
