query PageBySlug(
  $slug: String!
) {
  site {
    siteMetadata {
      title
      subtitle
    }
  }
  markdownRemark(fields: {slug: {eq: $slug}}) {
    id
    html
    frontmatter {
      title
      date
      description
    }
  }
}

query PostBySlug(
  $slug: String!
) {
  site {
    siteMetadata {
      author {
        name
        contacts {
          twitter
        }
      }
      disqusShortname
      subtitle
      title
      url
    }
  }
  markdownRemark(fields: {slug: {eq: $slug}}) {
    id
    html
    fields {
      tagSlugs
    }
    frontmatter {
      date
      description
      tags
      title
    }
  }
}

query TagPage(
  $tag: String
  $postsLimit: Int!
  $postsOffset: Int!
) {
  site {
    siteMetadata {
      title
      subtitle
    }
  }
  allMarkdownRemark(limit: $postsLimit, skip: $postsOffset, filter: {frontmatter: {tags: {in: [$tag]}, template: {eq: "post"}, draft: {ne: true}}}, sort: {order: DESC, fields: [frontmatter___date]}) {
    edges {
      node {
        fields {
          slug
          categorySlug
        }
        frontmatter {
          title
          date
          category
          description
        }
      }
    }
  }
}

query CategoryPage(
  $category: String
  $postsLimit: Int!
  $postsOffset: Int!
) {
  site {
    siteMetadata {
      title
      subtitle
    }
  }
  allMarkdownRemark(limit: $postsLimit, skip: $postsOffset, filter: {frontmatter: {category: {eq: $category}, template: {eq: "post"}, draft: {ne: true}}}, sort: {order: DESC, fields: [frontmatter___date]}) {
    edges {
      node {
        fields {
          categorySlug
          slug
        }
        frontmatter {
          date
          description
          category
          title
        }
      }
    }
  }
}

query IndexTemplate(
  $postsLimit: Int!
  $postsOffset: Int!
) {
  site {
    siteMetadata {
      title
      subtitle
    }
  }
  allMarkdownRemark(limit: $postsLimit, skip: $postsOffset, filter: {frontmatter: {template: {eq: "post"}, draft: {ne: true}}}, sort: {order: DESC, fields: [frontmatter___date]}) {
    edges {
      node {
        fields {
          slug
          categorySlug
        }
        frontmatter {
          title
          date
          category
          description
        }
      }
    }
  }
}

