     text-shadow: none;
     background: #b3d4fc;
 }

 pre[class*="language-"]::selection, pre[class*="language-"] ::selection,
 code[class*="language-"]::selection, code[class*="language-"] ::selection {
     text-shadow: none;
     background: #b3d4fc;
 }

 @media print {
     code[class*="language-"],
     pre[class*="language-"] {
         text-shadow: none;
     }
 }

 /* Code blocks */
 pre[class*="language-"] {
     padding: 1em;
     padding-top: 0rem;
     margin: .5em 0;
     overflow: auto;
 }

 :not(pre) > code[class*="language-"],
 pre[class*="language-"] {
     background: #f6f8fa;
 }

 /* Inline code */
 :not(pre) > code[class*="language-"] {
     padding: .1em;
     border-radius: .3em;
     white-space: normal;
 }

 .token.comment,
 .token.prolog,
 .token.doctype,
 .token.cdata {
     color: slategray;
 }

 .token.punctuation {
     color: #999;
 }

 .namespace {
     opacity: .7;
 }

 .token.property,
 .token.tag,
 .token.boolean,
 .token.number,
 .token.constant,
 .token.symbol,
 .token.deleted {
     color: #905;
 }

 .token.selector,
 .token.attr-name,
 .token.string,
 .token.char,
 .token.builtin,
 .token.inserted {
     color: #690;
 }

 .token.operator,
 .token.entity,
 .token.url,
 .language-css .token.string,
 .style .token.string {
     color: #9a6e3a;
     background: hsla(0, 0%, 100%, .5);
 }

 .token.atrule,
 .token.attr-value,
 .token.keyword {
     color: #07a;
 }

 .token.function,
 .token.class-name {
     color: #DD4A68;
 }

 .token.regex,
 .token.important,
 .token.variable {
     color: #e90;
 }

 .token.important,
 .token.bold {
     font-weight: bold;
 }
 .token.italic {
     font-style: italic;
 }

 .token.entity {
     cursor: help;
 }
