.keyfr01{
	animation-name:movingBox;
	animation-duration:2s;
	animation-iteration-count:infinite;
	animation-direction: alternate; /*set animate end reverse direction*/
}

@keyframes movingBox{
  0% {
    transform: translate(0, 0);
    opacity: 0.3;
  }

  25% {
